import React, { Component } from "react";
import Item from "./Item";
import "../App.css";

class Home extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: props.items
    }
  }


  render() {
    console.log(this.state.items, "List of items");

    const itemList =
    this.state.items &&
    this.state.items((item) => (
        <Item
          key={item.id}
          item={item}
          changeSize={item.changeSize}
          changeQuantity={item.changeQuantity}
          addToCart={item.addToCart}
          handleSubmit={item.handleSubmit}
        />
      ));

    console.log(itemList, "itemList");

    return (
      <div>
        <div className="tag">
          <button onClick={this.props.handleTag} value="top" className="button">
            Top
          </button>
          <button
            onClick={this.props.handleTag}
            value="bottom"
            className="button"
          >
            Bottom
          </button>
          <button onClick={this.props.handleTag} value="acc" className="button">
            Accessories
          </button>
          <button onClick={this.props.handleTag} value="all" className="button">
            All
          </button>
        </div>

        <div>{itemList}</div>
      </div>
    );
  }
}

export default Home;
