import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../App.css";

const Cart = (props) => {


  return (
    <div>
      <h1>Cart ({props.getTotalQuantity}):</h1>
      <div></div>
      <h3>Subtotal: ${props.getSubtotal}</h3>
      {props.cartLength > 0 && (
        <button type="submit" onClick={props.clearCart}>
          Clear
        </button>
      )}
    </div>
  );


}

export default Cart;
