import React, { Component } from "react";
import ItemList from "./ItemList";
import "../App.css";

const Home = (props, items, tagVisible) => {
  return (
    <div>
      <div className="tag">
        <button onClick={props.handleTag} value="top" className="button">
          Top
        </button>
        <button onClick={props.handleTag} value="bottom" className="button">
          Bottom
        </button>
        <button onClick={props.handleTag} value="acc" className="button">
          Accessories
        </button>
        <button onClick={props.handleTag} value="all" className="button">
          All
        </button>
      </div>

      <div>
        <ItemList 
        items={props.items} 

        />
      </div>
    </div>
  );
};

export default Home;
