import React, { Component } from "react";
import Item from "./Item";

import "../App.css";

const Home = (props) => {
  const itemList =
      props.items.map((item) => (
          <Item 
          key={item.id}
          item={item}
          />
        ));
  return (
    <div>
      <div className="tag">
        <button onClick={props.handleTag} value="top" className="button">
          Top
        </button>
        <button onClick={props.handleTag} value="bottom" className="button">
          Bottom
        </button>
        <button onClick={props.handleTag} value="acc" className="button">
          Accessories
        </button>
        <button onClick={props.handleTag} value="all" className="button">
          All
        </button>
      </div>

      <div>{itemList}</div>
    </div>
  );
};

export default Home;
