import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../App.css";

const Cart = (props) => {

  const cartList = props.cart.map((item) => {
    return (
      <fieldset>
        <p className="cart">
          {item.name}: {item.size}
        </p>
        <p>
          ${props.prices[item.name]} x{item.quantity}
        </p>
      </fieldset>
    );
  });

  return (
    <div>
      <h1>Cart ({props.getTotalQuantity}):</h1>
      {/* <div>{cartList}</div> */}
      <h3>Subtotal: ${props.getSubtotal}</h3>
      {props.cartLength > 0 && (
        <button type="submit" onClick={props.clearCart}>
          Clear
        </button>
      )}
    </div>
  );


}

export default Cart;
