import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../App.css";

function Cart() {
  const cartList = this.props.state.cart.map((item) => {
    return (
      <fieldset>
        <p className="cart">
          {item.name}: {item.size}
        </p>
        {/* <p>
          ${prices[item.name]} x{item.quantity}
        </p> */}
      </fieldset>
    );
  });

  return (
    <div>
      <h1>Cart ({this.props.getTotalQuantity()}):</h1>
      <div>{cartList}</div>
      <h3>Subtotal: ${this.props.getSubtotal()}</h3>
      {this.props.state.cart.length > 0 && (
        <button type="submit" onClick={this.props.clearCart}>
          Clear
        </button>
      )}
    </div>
  );
}

export default Cart;
