import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "./App.css";
import Home from "./components/Home";
import Nav from "./components/Nav";
import About from "./components/About";
import Cart from "./components/Cart";
import ItemData from "./components/ItemData";

const prices = {
  top1: 20,
  top2: 30,
  top3: 15,
  top4: 25,
  top5: 25,
  top6: 30,
  top7: 30,
  top8: 35,
  top9: 35,
  top10: 40,
  top11: 30,
  bottom: 45,
  acc1: 15,
  acc2: 15,
  acc3: 20,
  acc4: 20,
};

class App extends Component {
  state = {
    items: ItemData,
    cart: [],
    total: {},
    tagVisible: "all",
  };

  handleTag = (event) => {
    this.setState({ tagVisible: event.target.value });
  };

  changeSize = (id) => (event) => {
    console.log(event.target.value);
    let copyItems = [];
    this.state.items.forEach((item) => {
      let copyItem = { ...item };
      if (item.id === id) {
        copyItem.size = event.target.value;
      }
      console.log("copyItem", copyItem);
      copyItems.push(copyItem);
    });
    this.setState({ items: copyItems });
  };
  changeQuantity = (id) => (event) => {
    console.log(event.target.value);
    let copyItems = [];
    this.state.items.forEach((item) => {
      let copyItem = { ...item };
      if (item.id === id) {
        copyItem.quantity = parseInt(event.target.value);
      }
      console.log("copyItem", copyItem);
      copyItems.push(copyItem);
    });
    this.setState({ items: copyItems });
  };

  addToCart = (id) => () => {
    let newShoppingCart = [];
    let itemToAdd = {};
    console.log(this.state.items);
    this.state.items.forEach((item) => {
      if (item.id === id) {
        itemToAdd = item;
      }
    });
    let isExistingItemInCart = false;
    this.state.cart.forEach((item) => {
      if (item.id === itemToAdd.id && item.size === itemToAdd.size) {
        itemToAdd.quantity = item.quantity + itemToAdd.quantity;
        newShoppingCart.push(itemToAdd);
        isExistingItemInCart = true;
      } else {
        newShoppingCart.push(item);
      }
    });
    if (!isExistingItemInCart) {
      newShoppingCart.push(itemToAdd);
    }
    this.setState({ cart: newShoppingCart });
  };

  clearCart = () => {
    this.setState({ cart: [] });
  };

  getSubtotal = () => {
    let subtotal = 0;
    this.state.cart.forEach((item) => {
      subtotal = subtotal + item.quantity * prices[item.name];
    });
    return subtotal;
  };

  //want to get total quantity for total item in cart
  getTotalQuantity = () => {
    let totalQuantity = 0;
    this.state.cart.forEach((item) => {
      totalQuantity = item.quantity + totalQuantity;
    });
    return totalQuantity;
  };

  handleSubmit(e) {
    e.preventDefault();
    e.target.reset();
  }

  render() {
    return (
      <Router>
        <Nav />
        <Switch>
          <Route path="/" component={Home} exact />
          <Route path="/about" component={About} />
          <Route path="/cart" component={Cart} />
        </Switch>
      </Router>
    );
  }
}

export default App;
