import React, { Component } from "react";
import { prices } from "../constants/Prices";
import "../App.css";

const Cart = (props) => {

  const cartList =
    props.cart &&
    props.cart.map((item) => {
      return (
        <fieldset>
          <p className="cart">
            {item.name}: {item.size}
          </p>
          <p>
            ${prices[item.name]} x{item.quantity}
          </p>
        </fieldset>
      );
    });

  console.log(props.getTotalQuantity(), "total quantity");
  console.log(props.cartLength, "cartLength");

  const totalCart = () => {
    return(
      <div>
        <p4>Subtotal: </p4>
        <p>${props.getSubtotal()}</p>
        <p4>Shipping: </p4>
        <p>$4.99</p>
        <p4>Tax: </p4>
        <p>${props.getSubtotal() * 8.875}</p>
      </div>
    )
  }

  return (
    <div>
      <h1>CART ({props.getTotalQuantity()}):</h1>
      <div>{cartList}</div>
      <div>{totalCart}</div>

      {props.cartLength > 0 ? (
        <div>
          <h3>Subtotal: ${props.getSubtotal()}</h3>
          <button type="submit" onClick={props.clearCart}>
            Clear
          </button>
        </div>
      ) : (
        <div>
          <p>Your Cart is Empty</p>
        </div>
      )}
    </div>
  );
};

export default Cart;
