import React, { Component } from "react";
import Item from "./Item";

import "../App.css";

class Home extends Component {
  constructor() {
    super();
  }
  render() {
    itemList = this.props.items.map((item) => (
      <Item
        key={item.id}
        item={item}
        changeSize={this.props.changeSize}
        changeQuantity={this.props.changeSize}
        addToCart={this.props.addToCart}
        getSubtotal={this.props.getSubtotal}
        getTotalQuantity={this.props.getTotalQuantity}
        handleSubmit={this.props.handleSubmit}
      />
    ));
    return (
      <div>
        <div className="tag">
          <button onClick={props.handleTag} value="top" className="button">
            Top
          </button>
          <button onClick={props.handleTag} value="bottom" className="button">
            Bottom
          </button>
          <button onClick={props.handleTag} value="acc" className="button">
            Accessories
          </button>
          <button onClick={props.handleTag} value="all" className="button">
            All
          </button>
        </div>

        <div>{itemList}</div>
      </div>
    );
  }
}

export default Home;
