  top10: 40,
  top11: 30,
  bottom: 45,
  acc1: 15,
  acc2: 15,
  acc3: 20,
  acc4: 20,
};

class App extends Component {
  state = {
    items: [
      {
        id: 1,
        name: "top1",
        size: "",
        quantity: 0,
        tag: "top",
        img: "./images/top1.png",
      },
      {
