        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top7.png",
      },
      {
        id: 8,
        name: "top8",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top8.png",
      },
      {
        id: 9,
        name: "top9",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top9.png",
      },
      {
        id: 10,
        name: "top10",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top10.png",
      },
      {
        id: 11,
        name: "top11",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top11.png",
      },
      {
        id: 12,
        name: "bottom",
        size: "",
        price: 0,
        quantity: 0,
        tag: "bottom",
        img: "./images/bottom.png",
      },
      {
        id: 13,
        name: "acc1",
        size: "",
        price: 0,
        quantity: 0,
        tag: "acc",
        img: "./images/acc1.png",
      },
      {
        id: 14,
        name: "acc2",
        size: "",
        price: 0,
        quantity: 0,
        tag: "acc",
        img: "./images/acc2.png",
      },
      {
        id: 15,
        name: "acc3",
        size: "",
        price: 0,
        quantity: 0,
        tag: "acc",
        img: "./images/acc3.png",
      },
      {
        id: 16,
        name: "acc4",
        size: "",
        price: 0,
        quantity: 0,
        tag: "acc",
        img: "./images/acc4.png",
      },
    ],
    cart: [],
    total: {},
    tagVisible: "all",
  };

  handleTag = (event) => {
    this.setState({ tagVisible: event.target.value });
  };

  changeSize = (id) => (event) => {
    console.log(event.target.value);
    let copyItems = [];
    this.state.items.forEach((item) => {
      let copyItem = { ...item };
      if (item.id === id) {
        copyItem.size = event.target.value;
      }
      console.log("copyItem", copyItem);
      copyItems.push(copyItem);
    });
    this.setState({ items: copyItems });
  };
  changeQuantity = (id) => (event) => {
    console.log(event.target.value);
    let copyItems = [];
    this.state.items.forEach((item) => {
      let copyItem = { ...item };
      if (item.id === id) {
        copyItem.quantity = parseInt(event.target.value);
      }
      console.log("copyItem", copyItem);
      copyItems.push(copyItem);
    });
    this.setState({ items: copyItems });
  };

  addToCart = (id) => () => {
    let newShoppingCart = [];
    let itemToAdd = {};
    console.log(this.state.items);
    this.state.items.forEach((item) => {
      if (item.id === id) {
        itemToAdd = item;
      }
    });
    let isExistingItemInCart = false;
    this.state.cart.forEach((item) => {
      if (item.id === itemToAdd.id && item.size === itemToAdd.size) {
        itemToAdd.quantity = item.quantity + itemToAdd.quantity;
        newShoppingCart.push(itemToAdd);
        isExistingItemInCart = true;
      } else {
        newShoppingCart.push(item);
      }
    });
    if (!isExistingItemInCart) {
      newShoppingCart.push(itemToAdd);
    }
    this.setState({ cart: newShoppingCart });
  };

  clearCart = () => {
    this.setState({ cart: [] });
  };

  getSubtotal = () => {
    let subtotal = 0;
    this.state.cart.forEach((item) => {
      subtotal = subtotal + item.quantity * prices[item.name];
    });
    return subtotal;
  };

  //want to get total quantity for total item in cart
  getTotalQuantity = () => {
    let totalQuantity = 0;
    this.state.cart.forEach((item) => {
      totalQuantity = item.quantity + totalQuantity;
    });
    return totalQuantity;
  };

  handleSubmit(e) {
    e.preventDefault();
    e.target.reset();
  }

  //////
  // todos={
  //   this.state.colorVisible === "all"
  //     ? this.state.todos
  //     : this.state.todos.filter(
  //         (todoColor) => todoColor.tag === this.state.colorVisible
  //       )
  // }
  ///////

  render() {
    return (
      <Router>
        <Nav />
        <Switch>
              <Route path="/" component={App} exact />
              <Route path="/about" component={About} />
              <Route path="/cart" component={Cart} />
          </Switch>

      </Router>

  )

  }

}

export default App;
