import React, { Component } from "react";
import { prices } from "../constants/Prices";
import "../App.css";

const Cart = (props) => {

  const cartList =
    props.cart &&
    props.cart.map((item) => {
      return (
          <div className="cart-item">
                <img src={item.img} style={{ height: 150 }} />
                <p>{item.name}</p>
                <p>{item.size}</p>
                <p>${prices[item.name]}</p>
                <p>{item.quantity}</p>
                <p>Total</p>
              </div>

      );
    });
  console.log(props.getTotalQuantity(), "total quantity");
  console.log(props.cartLength, "cartLength");
  let tax = props.getSubtotal() * 0.08875;
  return (
    <div>
      <h1>CART ({props.getTotalQuantity()}):</h1>

      <div>
        {props.cartLength > 0 ? (
          <div className="cart">
            <div className="cart-left">
              <div className="cart-label">
                <h4></h4>
                <h4>Item</h4>
                <h4>Size</h4>
                <h4>Price</h4>
                <h4>Quantity</h4>
                <h4>Total</h4>
              </div>
              <div>{cartList}</div>
              <h3>Subtotal: ${props.getSubtotal()}</h3>
              <button type="submit" onClick={props.clearCart}>
                Clear
              </button>
            </div>
            <div className="cart-right">
              <div className="cart-right-inside">
                <div className="cart-right-left">
                  <p4>Subtotal:</p4>
                  <br />
                  <p4>Shipping:</p4>
                  <br />
                  <p4>Tax:</p4>
                  <br />
                  <p4>Total:</p4>
                </div>
                <div className="cart-right-right">
                  <p4>${props.getSubtotal()}</p4>
                  <br />
                  <p4>$4.99</p4>
                  <br />
                  <p4>${tax}</p4>
                  <br />
                  <p4>${props.getSubtotal() + 4.99 + tax}</p4>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div>
            <p>Your Cart is Empty</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
