      },
      {
        id: 4,
        name: "top4",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top4.png",
      },
      {
        id: 5,
        name: "top5",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top5.png",
      },
      {
        id: 6,
        name: "top6",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top6.png",
      },
      {
        id: 7,
        name: "top7",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top7.png",
      },
      {
        id: 8,
        name: "top8",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top8.png",
      },
      {
        id: 9,
        name: "top9",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top9.png",
      },
      {
        id: 10,
        name: "top10",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top10.png",
      },
      {
        id: 11,
        name: "top11",
        size: "",
        price: 0,
        quantity: 0,
        tag: "top",
        img: "./images/top11.png",
      },
      {
        id: 12,
        name: "bottom",
        size: "",
        price: 0,
        quantity: 0,
        tag: "bottom",
        img: "./images/bottom.png",
      },
      {
        id: 13,
        name: "acc1",
        size: "",
        price: 0,
        quantity: 0,
        tag: "acc",
        img: "./images/acc1.png",
      },
      {
        id: 14,
        name: "acc2",
        size: "",
        price: 0,
        quantity: 0,
        tag: "acc",
        img: "./images/acc2.png",
      },
      {
        id: 15,
        name: "acc3",
        size: "",
        price: 0,
        quantity: 0,
        tag: "acc",
        img: "./images/acc3.png",
      },
      {
        id: 16,
        name: "acc4",
        size: "",
        price: 0,
        quantity: 0,
        tag: "acc",
        img: "./images/acc4.png",
      },
    ],
  });

  const handleTag = (event) => {
    setTagVisible(event.target.value);
  };

  const changeSize = (id) => (event) => {
    console.log(event.target.value);
    let copyItems = [];
    state.items.forEach((item) => {
      let copyItem = { ...item };
      if (item.id === id) {
        copyItem.size = event.target.value;
      }
      console.log("copyItem", copyItem);
      copyItems.push(copyItem);
    });
    setState({ ...state, items: copyItems });
  };

  const changeQuantity = (id) => (event) => {
    console.log(event.target.value);
    let copyItems = [];
    state.items.forEach((item) => {
      let copyItem = { ...item };
      if (item.id === id) {
        copyItem.quantity = parseInt(event.target.value);
      }
      console.log("copyItem", copyItem);
      copyItems.push(copyItem);
    });
    setState({ ...state, items: copyItems });
  };

  const addToCart = (id) => () => {
    let newShoppingCart = [];
    let itemToAdd = {};
    state.items.forEach((item) => {
      if (item.id === id) {
        itemToAdd = item;
      }
    });
    let isExistingItemInCart = false;

    cart.forEach((item) => {
      if (item.id === itemToAdd.id && item.size === itemToAdd.size) {
        itemToAdd.quantity = item.quantity + itemToAdd.quantity;
        newShoppingCart.push(itemToAdd);
        isExistingItemInCart = true;
      } else {
        newShoppingCart.push(item);
      }
    });

    if (!isExistingItemInCart) {
      newShoppingCart.push(itemToAdd);
    }

    let copyItems = [];
    state.items.forEach((item) => {
      let copyItem = { ...item };
      if (item.id === id) {
        copyItem.size = "";
        copyItem.quantity = 0;
      }
      copyItems.push(copyItem);
    });

    setState({ ...state, items: copyItems });
    setCart(newShoppingCart);
  };

  const clearCart = () => {
    setCart([]);
  };

  const clickTrash = (id) => {
    let copyCart = [...cart];
    let result = copyCart.filter((item) => item.id !== id);
    setCart(result);
  };
  ///////

  ///////
  const clickMinus = (id) => {
    console.log(id, "minus clicked");
    let copyCart = [...cart];
    let itemSelected = {}
    copyCart.forEach((item) => {
      if (item.id === id) {
        itemSelected = item;
      }
    });
    let copyCart2 = [...copyCart];
    copyCart2.forEach((item) => {
      if (item.id === itemSelected.id && item.size === itemSelected.size && item.quantity > 0) {
        itemSelected.quantity--;
        console.log(item.id, "item.id")
        console.log(item.size, "item.size")
      } 
    });
    setCart(copyCart);
  };
  const clickPlus = (id) => {
    console.log(id, "plus clicked");
    let copyCart = [...cart];
    let itemSelected = {}
    let sSize = ""
    let sId = ""
    copyCart.forEach((item) => {
      if (item.id === id) {
        sSize = item.size;
        sId = item.id
      } 
    });
    let copyCart2 = [...copyCart];
    copyCart2.forEach((item) => {
      if (item.id === sId && item.size === sSize) {
        itemSelected.quantity++;

        console.log(item.id, "item.id")
        console.log(item.size, "item.size")
      } 
    });
    setCart(copyCart);
  };

  const getSubtotal = () => {
    let subtotal = 0;
    cart.forEach((item) => {
      subtotal = subtotal + item.quantity * prices[item.name];
    });
    return subtotal;
  };

  //want to get total quantity for total item in cart
  const getTotalQuantity = () => {
    let totalQuantity = 0;
    cart.forEach((item) => {
      totalQuantity = item.quantity + totalQuantity;
    });
    return totalQuantity;
  };

  return (
    <div>
      <Router>
        <Route render={() => <Nav getTotalQuantity={getTotalQuantity} />} />
        <Switch>
          <Route
            path="/"
            exact
            render={() => (
              <Home
                listOfItems={state.items}
                tagVisible={tagVisible}
                handleTag={handleTag}
                changeSize={changeSize}
                changeQuantity={changeQuantity}
                addToCart={addToCart}
              />
            )}
          />

          <Route path="/about" component={About} />

          <Route
            path="/cart"
            exact
            render={() => (
              <Cart
                cart={cart}
                cartLength={cart.length}
                getSubtotal={getSubtotal}
                getTotalQuantity={getTotalQuantity}
                clearCart={clearCart}
                setCart={setCart}
                clickTrash={clickTrash}
                clickMinus={clickMinus}
                clickPlus={clickPlus}
              />
            )}
          />
        </Switch>
      </Router>
    </div>
  );
};

export default App;
