import React, { Component } from "react";
import { prices } from "../constants/Prices";
import "../App.css";

const Cart = (props) => {
  const cartList =
    props.cart &&
    props.cart.map((item) => {
      const cartEmpty = false;
      if (item.quantity == 0){
        cartEmpty = true;
      }
      return (
        <div>
          {item.quantity > 0 && (
            <div className="cart-item">
              <img src={item.img} style={{ height: 70 }} />
              <p>{item.name}</p>
              <p>{item.size}</p>
              <p>${prices[item.name]}</p>
              <p>
                <i
                  onClick={() => props.clickMinus(item.id, item.size)}
                  class="fas fa-minus-circle"
                ></i>
                {item.quantity}
                <i
                  onClick={() => props.clickPlus(item.id, item.size)}
                  class="fas fa-plus-circle"
                ></i>
              </p>
              <p>${prices[item.name] * item.quantity}</p>
              <i
                onClick={() => props.clickTrash(item.id, item.size)}
                class="fas fa-trash-alt fa-lg"
                style={{ color: "darkred" }}
              ></i>
            </div>
          )}
        </div>
      );
    });

  let tax = props.getSubtotal() * 0.08875;

  return (
    <div>
      <h1>CART ({props.getTotalQuantity()}):</h1>

      <div>
        {props.cartLength > 0 && !cartEmpty ?(
          <div className="cart">
            <div className="cart-left">
              <div className="cart-label">
                <h4></h4>
                <h4>Item</h4>
                <h4>Size</h4>
                <h4>Price</h4>
                <h4>Quantity</h4>
                <h4>Total</h4>
                <h4></h4>
              </div>
              <div>{cartList}</div>

              <h3>Subtotal: ${props.getSubtotal()}</h3>
              <button type="submit" onClick={props.clearCart}>
                Clear
              </button>
            </div>
            <div className="cart-right">
              <div className="cart-right-inside">
                <div className="cart-right-left">
                  <p4>Subtotal:</p4>
                  <br />
                  <p4>Shipping:</p4>
                  <br />
                  <p4>Tax:</p4>
                  <br />
                  <br />
                  <p4>Total:</p4>
                </div>
                <div className="cart-right-right">
                  <p4>${props.getSubtotal()}.00</p4>
                  <br />
                  <p4>$4.99</p4>
                  <br />
                  <p4>${tax.toFixed(2)}</p4>
                  <br />
                  <br />
                  <p4>${(props.getSubtotal() + 4.99 + tax).toFixed(2)}</p4>
                </div>
              </div>
            </div>
          </div>
        ) : (
          <div>
            <p>Your Cart is Empty</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
