          quantity: 0,
          tag: "top",
          img: "./images/top3.png",
        },
        {
          id: 4,
          name: "top4",
          size: "",
          price: 0,
          quantity: 0,
          tag: "top",
          img: "./images/top4.png",
        },
        {
          id: 5,
          name: "top5",
          size: "",
          price: 0,
          quantity: 0,
          tag: "top",
          img: "./images/top5.png",
        },
        {
          id: 6,
          name: "top6",
          size: "",
          price: 0,
          quantity: 0,
          tag: "top",
          img: "./images/top6.png",
        },
        {
          id: 7,
          name: "top7",
          size: "",
          price: 0,
          quantity: 0,
          tag: "top",
          img: "./images/top7.png",
        },
        {
          id: 8,
          name: "top8",
          size: "",
          price: 0,
          quantity: 0,
          tag: "top",
          img: "./images/top8.png",
        },
        {
          id: 9,
          name: "top9",
          size: "",
          price: 0,
          quantity: 0,
          tag: "top",
          img: "./images/top9.png",
        },
        {
          id: 10,
          name: "top10",
          size: "",
          price: 0,
          quantity: 0,
          tag: "top",
          img: "./images/top10.png",
        },
        {
          id: 11,
          name: "top11",
          size: "",
          price: 0,
          quantity: 0,
          tag: "top",
          img: "./images/top11.png",
        },
        {
          id: 12,
          name: "bottom",
          size: "",
          price: 0,
          quantity: 0,
          tag: "bottom",
          img: "./images/bottom.png",
        },
        {
          id: 13,
          name: "acc1",
          size: "",
          price: 0,
          quantity: 0,
          tag: "acc",
          img: "./images/acc1.png",
        },
        {
          id: 14,
          name: "acc2",
          size: "",
          price: 0,
          quantity: 0,
          tag: "acc",
          img: "./images/acc2.png",
        },
        {
          id: 15,
          name: "acc3",
          size: "",
          price: 0,
          quantity: 0,
          tag: "acc",
          img: "./images/acc3.png",
        },
        {
          id: 16,
          name: "acc4",
          size: "",
          price: 0,
          quantity: 0,
          tag: "acc",
          img: "./images/acc4.png",
        },
      ],
      cart: [],
      total: {},
      tagVisible: "all",
    };
    this.handleTag = this.handleTag.bind(this);
    this.changeSize = this.changeSize.bind(this);
    this.changeQuantity = this.changeQuantity.bind(this);
    this.addToCart = this.addToCart.bind(this);
    this.clearCart = this.clearCart.bind(this);
    this.getSubtotal = this.getSubtotal.bind(this);
    this.getTotalQuantity = this.getTotalQuantity.bind(this);
  }

  handleTag = (event) => {
    this.setState({ tagVisible: event.target.value });
    console.log("handle tag!")
  };

  changeSize = (id) => (event) => {
    console.log(event.target.value);
    let copyItems = [];
    this.state.items.forEach((item) => {
      let copyItem = { ...item };
      if (item.id === id) {
        copyItem.size = event.target.value;
      }
      console.log("copyItem", copyItem);
      copyItems.push(copyItem);
    });
    this.setState({ items: copyItems });
  };

  changeQuantity = (id) => (event) => {
    console.log(event.target.value);
    let copyItems = [];
    this.state.items.forEach((item) => {
      let copyItem = { ...item };
      if (item.id === id) {
        copyItem.quantity = parseInt(event.target.value);
      }
      console.log("copyItem", copyItem);
      copyItems.push(copyItem);
    });
    this.setState({ items: copyItems });
  };

  addToCart = (id) => () => {
    let newShoppingCart = [];
    let itemToAdd = {};
    console.log(this.state.items);
    this.state.items.forEach((item) => {
      if (item.id === id) {
        itemToAdd = item;
      }
    });
    let isExistingItemInCart = false;
    this.state.cart.forEach((item) => {
      if (item.id === itemToAdd.id && item.size === itemToAdd.size) {
        itemToAdd.quantity = item.quantity + itemToAdd.quantity;
        newShoppingCart.push(itemToAdd);
        isExistingItemInCart = true;
      } else {
        newShoppingCart.push(item);
      }
    });
    if (!isExistingItemInCart) {
      newShoppingCart.push(itemToAdd);
    }
    this.setState({ cart: newShoppingCart });
  };

  clearCart = () => {
    this.setState({ cart: [] });
  };

  getSubtotal = () => {
    let subtotal = 0;
    this.state.cart.forEach((item) => {
      subtotal = subtotal + item.quantity * prices[item.name];
    });
    return subtotal;
  };

  //want to get total quantity for total item in cart
  getTotalQuantity = () => {
    let totalQuantity = 0;
    this.state.cart.forEach((item) => {
      totalQuantity = item.quantity + totalQuantity;
    });
    return totalQuantity;
  };

  handleSubmit(e) {
    e.preventDefault();
    e.target.reset();
  }

  render() {
    const itemList =
      this.state.tagVisible === "all"
        ? this.state.items.map((item) => {
            return (
              <div key={item.id} className="column">
                <form onSubmit={this.handleSubmit.bind(this)}>
                  <fieldset>
                    <h3>
                      {item.name} (${prices[item.name]})
                    </h3>
                    <img src={item.img} style={{ height: 150 }} />
                    <br />
                    <label>
                      Size:
                      <select
                        value={item.size}
                        name="size"
                        onChange={this.changeSize(item.id)}
                      >
                        <option value="">Select Size</option>
                        <option value="small">Small</option>
                        <option value="medium">Medium</option>
                        <option value="large">Large</option>
                      </select>
                    </label>
                    <br />
                    <label>
                      Quantity:
                      <input
                        type="number"
                        value={item.quantity}
                        min="1"
                        onChange={this.changeQuantity(item.id)}
                      />
                    </label>
                    <button
                      type="submit"
                      onClick={this.addToCart(item.id)}
                      className="button"
                    >
                      Add to Cart
                    </button>
                  </fieldset>
                </form>
              </div>
            );
          })
        : this.state.items
            .filter((item) => item.tag === this.state.tagVisible)
            .map((item) => {
              return (
                <div key={item.id} className="column">
                  <form onSubmit={this.handleSubmit.bind(this)}>
                    <fieldset>
                      <h3>
                        {item.name} (${prices[item.name]})
                      </h3>
                      <img src={item.img} style={{ height: 150 }} />
                      <br />
                      <label>
                        Size:
                        <select
                          value={item.size}
                          name="size"
                          onChange={this.changeSize(item.id)}
                        >
                          <option value="">Select Size</option>
                          <option value="small">Small</option>
                          <option value="medium">Medium</option>
                          <option value="large">Large</option>
                        </select>
                      </label>
                      <br />
                      <label>
                        Quantity:
                        <input
                          type="number"
                          value={item.quantity}
                          min="1"
                          onChange={this.changeQuantity(item.id)}
                        />
                      </label>
                      <button
                        type="submit"
                        onClick={this.addToCart(item.id)}
                        className="button"
                      >
                        Add to Cart
                      </button>
                    </fieldset>
                  </form>
                </div>
              );
            });

    return (
      <div>
        <Home
          listOfItems={this.state.items}
          tagVisible={this.state.tagVisible}
          handleTag={this.handleTag}
          changeSize={this.changeSize}
          changeQuantity={this.changeSize}
          addToCart={this.addToCart}
          handleSubmit={this.handleSubmit}
        >
          {itemList}
          </Home>/>

        <Cart
          cart={this.state.cart}
          cartLength={this.state.cart.length}
          getSubtotal={this.getSubtotal}
          getTotalQuantity={this.getTotalQuantity}
          clearCart={this.clearCart}
        />

        <About />
      </div>
    );
  }
}

export default App;
