import React from "react";
import "../App.css";
import { prices } from "../constants/Prices";

const Item = (props) => {
  return (
    <div key={props.key} className="column">
      <div className="item">
        <h3>
          {props.item.name} (${prices[props.item.name]})
        </h3>
        <img src={props.item.img} style={{ height: 150 }} />
        <br />
        <label className="selectSize">
          Size:
          <select
            value={props.item.size}
            name="size"
            onChange={props.changeSize(props.item.id)}
          >
            <option value="">Select Size</option>
            <option value="small">Small</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
          </select>
        </label>
        <br />
        <label className="selectQuantity">
          Quantity:
          <input
            type="number"
            value={props.item.quantity}
            min="1"
            onChange={props.changeQuantity(props.item.id)}
          />
        </label>
        <button
          type="submit"
          onClick={props.addToCart(props.item.id)}
          className="button"
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
};

export default Item;
