import React, { Component } from "react";
import Item from "./Item";

import "../App.css";

class Home extends Component {
  constructor() {
    super();
  }
  render() {
    console.log(this.props.listOfItems, "List of items");
    const itemList =
    this.props.listOfItems &&
    this.props.listOfItems.map((item) => (
        <Item
          key={item.id}
          item={item}
          changeSize={props.changeSize}
          changeQuantity={props.changeQuantity}
          addToCart={props.addToCart}
          handleSubmit={props.handleSubmit}
        />
      ));
    console.log(itemList, "itemList");

    return (
      <div>
        <div className="tag">
          <button onClick={this.props.handleTag} value="top" className="button">
            Top
          </button>
          <button onClick={this.props.handleTag} value="bottom" className="button">
            Bottom
          </button>
          <button onClick={this.props.handleTag} value="acc" className="button">
            Accessories
          </button>
          <button onClick={this.props.handleTag} value="all" className="button">
            All
          </button>
        </div>

        <div>{itemList}</div>
      </div>
    );
  }
}

export default Home;
