import React, { Component } from "react";
import "../App.css";

class Item extends Component {
  constructor() {
    super();
  }
  render() {
    return (
      <div key={props.item.id} className="column">
        <form onSubmit={props.handleSubmit.bind(this)}>
          <fieldset>
            <h3>{/* {item.name} (${prices[item.name]}) */}</h3>
            <img src={props.item.img} style={{ height: 150 }} />
            <br />
            <label>
              Size:
              <select
                value={props.item.size}
                name="size"
                onChange={props.changeSize(props.item.id)}
              >
                <option value="">Select Size</option>
                <option value="small">Small</option>
                <option value="medium">Medium</option>
                <option value="large">Large</option>
              </select>
            </label>
            <br />
            <label>
              Quantity:
              <input
                type="number"
                value={props.item.quantity}
                min="1"
                onChange={props.changeQuantity(props.item.id)}
              />
            </label>
            <button
              type="submit"
              onClick={props.addToCart(props.item.id)}
              className="button"
            >
              Add to Cart
            </button>
          </fieldset>
        </form>
      </div>
    );
  }
}

export default Item;
